import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import './GraphViewer.css';

const GraphViewerClean = () => {
  const svgRef = useRef(null);
  const simulationRef = useRef(null);
  const [selectedPapers, setSelectedPapers] = useState(new Set());
  const [showAIPanel, setShowAIPanel] = useState(false);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const [searchQuery, setSearchQuery] = useState('');

  // Mock data
  const mockPapers = [
    {
      id: 'paper1',
      title: 'Deep Learning for Computer Vision',
      authors: ['John Smith', 'Jane Doe'],
      year: 2023,
      citations: 150,
      topics: ['Computer Vision', 'Deep Learning', 'CNN']
    },
    {
      id: 'paper2',
      title: 'Neural Networks in Natural Language Processing',
      authors: ['Alice Johnson', 'Bob Wilson'],
      year: 2022,
      citations: 89,
      topics: ['NLP', 'Neural Networks', 'Language Models']
    },
    {
      id: 'paper3',
      title: 'Reinforcement Learning Applications',
      authors: ['Charlie Brown', 'Diana Prince'],
      year: 2023,
      citations: 67,
      topics: ['Reinforcement Learning', 'AI', 'Robotics']
    },
    {
      id: 'paper4',
      title: 'Graph Neural Networks',
      authors: ['Eve Adams', 'Frank Miller'],
      year: 2021,
      citations: 234,
      topics: ['Graph Neural Networks', 'Deep Learning', 'Graph Theory']
    },
    {
      id: 'paper5',
      title: 'Transformer Architecture',
      authors: ['Grace Lee', 'Henry Davis'],
      year: 2022,
      citations: 312,
      topics: ['Transformers', 'Attention Mechanism', 'NLP']
    }
  ];

  const mockCitations = [
    { source: 'paper1', target: 'paper2' },
    { source: 'paper1', target: 'paper3' },
    { source: 'paper2', target: 'paper4' },
    { source: 'paper3', target: 'paper5' },
    { source: 'paper4', target: 'paper5' }
  ];

  // Search functionality
  const filteredPapers = mockPapers.filter(paper =>
    paper.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    paper.authors.some(author => author.toLowerCase().includes(searchQuery.toLowerCase())) ||
    paper.topics.some(topic => topic.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  // Event handlers
  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleGraphClick = (event) => {
    if (event.target === event.currentTarget) {
      setSelectedPapers(new Set());
      setShowAIPanel(false);
    }
  };

  const handleExport = () => {
    console.log('Exporting selected papers:', Array.from(selectedPapers));
  };

  // Update dimensions on window resize
  useEffect(() => {
    const updateDimensions = () => {
      setDimensions({
        width: window.innerWidth - (showAIPanel ? 300 : 0),
        height: window.innerHeight
      });
    };

    // Set initial dimensions
    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, [showAIPanel]);

  // Create and update the graph - Clean D3 implementation
  useEffect(() => {
    if (!dimensions.width || !dimensions.height || mockPapers.length === 0) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove(); // Clear previous graph

    // Create main group
    const g = svg.append('g');

    // Create zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoom);

    // Prepare data
    const nodes = mockPapers.map(paper => ({
      id: paper.id,
      title: paper.title,
      authors: paper.authors,
      year: paper.year,
      citations: paper.citations
    }));

    const links = mockCitations.map(citation => ({
      source: citation.source,
      target: citation.target
    }));

    // Create force simulation
    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links).id(d => d.id).distance(100))
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(dimensions.width / 2, dimensions.height / 2));

    // Store simulation reference
    simulationRef.current = simulation;

    // Create links
    const link = g.append('g')
      .attr('class', 'links')
      .selectAll('line')
      .data(links)
      .enter().append('line')
      .attr('stroke', '#999')
      .attr('stroke-opacity', 0.6)
      .attr('stroke-width', 1);

    // Create nodes
    const node = g.append('g')
      .attr('class', 'nodes')
      .selectAll('circle')
      .data(nodes)
      .enter().append('circle')
      .attr('r', 8)
      .attr('data-selected', d => selectedPapers.has(d.id) ? 'true' : 'false')
      .attr('fill', d => selectedPapers.has(d.id) ? '#7c3aed' : '#3b82f6')
      .attr('stroke', d => selectedPapers.has(d.id) ? '#5b21b6' : '#1e40af')
      .attr('stroke-width', d => selectedPapers.has(d.id) ? 3 : 2)
      .style('cursor', 'pointer')
      .call(d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended));

    // Add labels
    const labels = g.append('g')
      .attr('class', 'labels')
      .selectAll('text')
      .data(nodes)
      .enter().append('text')
      .attr('text-anchor', 'middle')
      .attr('dy', 4)
      .style('font-size', '10px')
      .style('fill', '#333')
      .style('pointer-events', 'none')
      .text(d => `${d.authors[0]?.split(' ')[0] || 'Unknown'}, ${d.year}`);

    // Update positions on tick
    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      node
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);

      labels
        .attr('x', d => d.x)
        .attr('y', d => d.y);
    });

    // Drag functions
    function dragstarted(event) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      event.subject.fx = event.subject.x;
      event.subject.fy = event.subject.y;
    }

    function dragged(event) {
      event.subject.fx = event.x;
      event.subject.fy = event.y;
    }

    function dragended(event) {
      if (!event.active) simulation.alphaTarget(0);
      event.subject.fx = null;
      event.subject.fy = null;
    }

    // Hover handlers - using direct DOM manipulation to avoid re-renders
    node.on('mouseover', function(event, d) {
      // Create or update hover tooltip directly in DOM
      let tooltip = d3.select('body').select('.hover-tooltip');
      if (tooltip.empty()) {
        tooltip = d3.select('body').append('div')
          .attr('class', 'hover-tooltip')
          .style('position', 'absolute')
          .style('background', 'rgba(0, 0, 0, 0.8)')
          .style('color', 'white')
          .style('padding', '8px 12px')
          .style('border-radius', '4px')
          .style('font-size', '12px')
          .style('pointer-events', 'none')
          .style('z-index', '1000')
          .style('opacity', 0);
      }

      tooltip
        .html(`
          <div><strong>${d.title}</strong></div>
          <div>${d.authors.join(', ')}</div>
          <div>${d.year} ‚Ä¢ ${d.citations} citations</div>
        `)
        .style('left', (event.pageX + 10) + 'px')
        .style('top', (event.pageY - 10) + 'px')
        .transition()
        .duration(200)
        .style('opacity', 1);
    });

    node.on('mouseout', function(event, d) {
      d3.select('body').select('.hover-tooltip')
        .transition()
        .duration(200)
        .style('opacity', 0)
        .remove();
    });

    // Click handler - using direct DOM manipulation to avoid re-renders
    node.on('click', function(event, d) {
      // Get current selection from the node's data
      const isCurrentlySelected = d3.select(this).attr('data-selected') === 'true';

      // Toggle selection in the DOM
      d3.select(this)
        .attr('data-selected', !isCurrentlySelected)
        .attr('fill', !isCurrentlySelected ? '#7c3aed' : '#3b82f6')
        .attr('stroke', !isCurrentlySelected ? '#5b21b6' : '#1e40af')
        .attr('stroke-width', !isCurrentlySelected ? 3 : 2);

      // Update React state without causing re-render of D3 elements
      setSelectedPapers(prevSelected => {
        const newSelected = new Set(prevSelected);
        if (isCurrentlySelected) {
          newSelected.delete(d.id);
        } else {
          newSelected.add(d.id);
        }
        setShowAIPanel(newSelected.size > 0);
        return newSelected;
      });
    });

    // Cleanup
    return () => {
      if (simulationRef.current) {
        simulationRef.current.stop();
      }
      // Clean up any remaining tooltips
      d3.select('body').selectAll('.hover-tooltip').remove();
    };
  }, [mockPapers, mockCitations, dimensions]);

  return (
    <div className="graph-viewer-container">
      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => handleSearch(e.target.value)}
          placeholder="Enter Keyword, DOI"
          className="search-input"
        />
        <button className="search-btn">üîç</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* References Panel */}
        <div className="references-panel">
          <h3 className="panel-title">References</h3>

          <div className="references-list">
            {filteredPapers.map((paper, index) => (
              <div
                key={paper.id}
                className={`reference-item ${
                  selectedPapers.has(paper.id) ? 'active' : ''
                }`}
                onClick={() => {
                  setSelectedPapers(prevSelected => {
                    const newSelected = new Set(prevSelected);

                    if (newSelected.has(paper.id)) {
                      newSelected.delete(paper.id);
                    } else {
                      newSelected.add(paper.id);
                    }

                    // Update the corresponding node in the graph
                    const graphNode = d3.select(`circle[data-id="${paper.id}"]`);
                    if (!graphNode.empty()) {
                      const isSelected = newSelected.has(paper.id);
                      graphNode
                        .attr('data-selected', isSelected ? 'true' : 'false')
                        .attr('fill', isSelected ? '#7c3aed' : '#3b82f6')
                        .attr('stroke', isSelected ? '#5b21b6' : '#1e40af')
                        .attr('stroke-width', isSelected ? 3 : 2);
                    }

                    setShowAIPanel(newSelected.size > 0);
                    return newSelected;
                  });
                }}
              >
                <div className="reference-title">
                  {index + 1}. {paper.title}
                </div>
                <div className="reference-authors">
                  {paper.authors.join(', ')}
                </div>
                <div className="reference-year">
                  {paper.year} ‚Ä¢ {paper.citations} citations
                </div>
                <div className="reference-topics">
                  {paper.topics.slice(0, 3).map(topic => (
                    <span key={topic} className="topic-tag">
                      {topic}
                    </span>
                  ))}
                </div>
              </div>
            ))}

            {filteredPapers.length === 0 && (
              <div className="no-results">
                No papers found. Try a different search term.
              </div>
            )}
          </div>

          <button className="export-btn" onClick={handleExport}>
            Export
          </button>
        </div>

        {/* Graph Visualization */}
        <div className="graph-container" onClick={handleGraphClick}>
          {/* Selection Counter */}
          {selectedPapers.size > 0 && (
            <div className="selection-counter">
              {selectedPapers.size} selected
            </div>
          )}

          {/* Instructions */}
          <div className="graph-instructions">
            <div className="instruction-item">üñ±Ô∏è Click & drag to move nodes</div>
            <div className="instruction-item">üñ±Ô∏è Click to select/unselect nodes</div>
          </div>

          <svg
            ref={svgRef}
            width={dimensions.width}
            height={dimensions.height}
            className="graph-svg"
          />

        </div>

        {/* AI Panel */}
        {showAIPanel && (
          <div className="ai-panel">
            <h3>Selected Papers ({selectedPapers.size})</h3>
            <div className="selected-papers">
              {Array.from(selectedPapers).map(paperId => {
                const paper = mockPapers.find(p => p.id === paperId);
                return paper ? (
                  <div key={paperId} className="selected-paper">
                    <h4>{paper.title}</h4>
                    <p>{paper.authors.join(', ')} ({paper.year})</p>
                    <p>Citations: {paper.citations}</p>
                  </div>
                ) : null;
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GraphViewerClean;
