      id: 'paper3',
      title: 'Attention Mechanisms in Neural Networks',
      authors: ['Charlie Brown', 'Diana Prince'],
      year: 2023,
      abstract: 'This work investigates attention mechanisms and their effectiveness...',
      citations: 32,
      doi: '10.1000/test3',
      topics: ['Attention', 'Neural Networks', 'AI']
