    paper.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    paper.authors.some(author => 
      author.toLowerCase().includes(searchQuery.toLowerCase())
    ) ||
    paper.topics.some(topic => 
      topic.toLowerCase().includes(searchQuery.toLowerCase())
    )
  );

  // Update dimensions on window resize
  useEffect(() => {
    const updateDimensions = () => {
      const container = svgRef.current?.parentElement;
      if (container) {
        setDimensions({
          width: container.clientWidth,
          height: container.clientHeight
        });
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Create and update the graph
  useEffect(() => {
    if (!dimensions.width || !dimensions.height || mockPapers.length === 0) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove(); // Clear previous graph

    // Create main group for zoom/pan
    const g = svg.append('g');

    // Create zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoom);

    // Prepare data for D3
    const nodes = mockPapers.map(paper => ({
      id: paper.id,
      title: paper.title,
      authors: paper.authors,
      year: paper.year,
      citations: paper.citations,
      topics: paper.topics,
      ...paper
    }));

    const links = mockCitations.map(citation => ({
      source: citation.source,
      target: citation.target,
      type: citation.type
    }));

    // Create force simulation
    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links).id(d => d.id).distance(100))
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force('collision', d3.forceCollide().radius(30));

    // Create links
    const link = g.append('g')
      .attr('class', 'links')
      .selectAll('line')
      .data(links)
      .enter().append('line')
      .attr('class', 'link')
      .style('stroke', '#555')
      .style('stroke-opacity', 0.6)
      .style('stroke-width', 1);

    // Create nodes
    const node = g.append('g')
      .attr('class', 'nodes')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .attr('class', 'node')
      .call(d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended));

    // Add circles to nodes
    node.append('circle')
      .attr('r', d => Math.max(8, Math.min(20, Math.sqrt(d.citations) * 2)))
      .style('fill', d => {
        if (selectedPapers.has(d.id)) return '#7c3aed';
        if (hoveredNode?.id === d.id) return '#fbbf24';
        return '#6b7280';
      })
      .style('stroke', d => {
        if (selectedPapers.has(d.id)) return '#7c3aed';
        if (hoveredNode?.id === d.id) return '#fbbf24';
        return '#9ca3af';
      })
      .style('stroke-width', d => {
        if (selectedPapers.has(d.id) || hoveredNode?.id === d.id) return 3;
        return 1;
      });

    // Add labels to nodes
    node.append('text')
      .attr('class', 'node-label')
      .attr('dy', 4)
      .attr('text-anchor', 'middle')
      .style('font-size', '10px')
      .style('fill', '#ffffff')
      .style('pointer-events', 'none')
      .text(d => `${d.authors[0]?.split(' ')[0] || 'Unknown'}, ${d.year}`);

    // Add hover effects and interactions
    node
      .on('mouseover', function(event, d) {
        if (!isDragging) {
          setHoveredNode(d);

          // Highlight connected nodes
          const connectedNodes = new Set();
          links.forEach(link => {
            if (link.source.id === d.id) connectedNodes.add(link.target.id);
            if (link.target.id === d.id) connectedNodes.add(link.source.id);
          });

          node.style('opacity', n => 
            n.id === d.id || connectedNodes.has(n.id) ? 1 : 0.3
          );

          link.style('opacity', l => 
            l.source.id === d.id || l.target.id === d.id ? 1 : 0.1
          );
        }
      })
      .on('mouseout', function() {
        if (!isDragging) {
          setHoveredNode(null);

          node.style('opacity', 1);
          link.style('opacity', 0.6);
        }
      })
      .on('click', function(event, d) {
        event.stopPropagation();

        if (event.ctrlKey || event.metaKey) {
          // Multi-select with Ctrl/Cmd
          const newSelected = new Set(selectedPapers);
          if (newSelected.has(d.id)) {
            newSelected.delete(d.id);
          } else {
            newSelected.add(d.id);
          }
          setSelectedPapers(newSelected);
        } else {
          // Single select
          setSelectedPapers(new Set([d.id]));
          setShowAIPanel(true);
        }
      });

    // Update positions on simulation tick
    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      node.attr('transform', d => `translate(${d.x},${d.y})`);
    });

    // Drag functions - Obsidian-style behavior
    function dragstarted(event, d) {
      setIsDragging(true);

      // If not selected, select this node
      if (!selectedPapers.has(d.id)) {
        if (event.ctrlKey || event.metaKey) {
          const newSelected = new Set(selectedPapers);
          newSelected.add(d.id);
          setSelectedPapers(newSelected);
        } else {
          setSelectedPapers(new Set([d.id]));
        }
      }

      // Drag all selected nodes together
      selectedPapers.forEach(selectedId => {
        const selectedNode = nodes.find(n => n.id === selectedId);
        if (selectedNode) {
          selectedNode.fx = selectedNode.x;
          selectedNode.fy = selectedNode.y;
        }
      });

      if (!event.active) simulation.alphaTarget(0.3).restart();
    }

    function dragged(event, d) {
      const dx = event.x - d.x;
      const dy = event.y - d.y;

      // Move all selected nodes by the same offset
      selectedPapers.forEach(selectedId => {
        const selectedNode = nodes.find(n => n.id === selectedId);
        if (selectedNode) {
          selectedNode.fx = selectedNode.x + dx;
          selectedNode.fy = selectedNode.y + dy;
        }
      });
    }

    function dragended(event, d) {
      setIsDragging(false);

      // Keep selected nodes fixed in their new positions
      selectedPapers.forEach(selectedId => {
        const selectedNode = nodes.find(n => n.id === selectedId);
        if (selectedNode) {
          // Keep the node fixed at its current position
          selectedNode.fx = selectedNode.x;
          selectedNode.fy = selectedNode.y;
        }
      });

      if (!event.active) simulation.alphaTarget(0);
    }

    // Cleanup
    return () => {
      simulation.stop();
    };
  }, [mockPapers, mockCitations, selectedPapers, dimensions, hoveredNode, isDragging]);

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleExport = () => {
    const exportData = {
      papers: mockPapers,
      citations: mockCitations,
      timestamp: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json'
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'refnet-graph-export.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleSummarize = () => {
    if (selectedPaper && aiInput.trim()) {
      console.log(`Summarizing "${selectedPaper.title}" with query: "${aiInput}"`);
      // Here you would call your AI service
    }
  };

  return (
    <div className="graph-viewer-container">
      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => handleSearch(e.target.value)}
          placeholder="Enter Keyword, DOI"
          className="search-input"
        />
        <button className="search-btn">🔍</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* References Panel */}
        <div className="references-panel">
          <h3 className="panel-title">References</h3>

          <div className="references-list">
            {filteredPapers.map((paper, index) => (
              <div
                key={paper.id}
                className={`reference-item ${
                  selectedPaper?.id === paper.id ? 'active' : ''
                }`}
                onClick={() => {
                  setSelectedPaper(paper);
                  setShowAIPanel(true);
                }}
              >
                <div className="reference-title">
                  {index + 1}. {paper.title}
                </div>
                <div className="reference-authors">
                  {paper.authors.join(', ')}
                </div>
                <div className="reference-year">
                  {paper.year} • {paper.citations} citations
                </div>
                <div className="reference-topics">
                  {paper.topics.slice(0, 3).map(topic => (
                    <span key={topic} className="topic-tag">
                      {topic}
                    </span>
                  ))}
                </div>
              </div>
            ))}

            {filteredPapers.length === 0 && (
              <div className="no-results">
                No papers found. Try a different search term.
              </div>
            )}
          </div>

          <button className="export-btn" onClick={handleExport}>
            Export
          </button>
        </div>

        {/* Graph Visualization */}
        <div className="graph-container">
          <svg
            ref={svgRef}
            width={dimensions.width}
            height={dimensions.height}
            className="graph-svg"
          />

          {hoveredNode && (
            <div className="node-details visible">
              <div className="node-details-title">{hoveredNode.title}</div>
              <div className="node-details-authors">
                {hoveredNode.authors.join(', ')}
              </div>
              <div className="node-details-year">
                {hoveredNode.year} • {hoveredNode.citations} citations
              </div>
              <div className="node-details-topics">
                {hoveredNode.topics.map(topic => (
                  <span key={topic} className="topic-tag">
                    {topic}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Control Panel */}
        <div className="control-panel">
          <button
            className={`control-btn ${currentView === 'papers' ? 'active' : ''}`}
            onClick={() => setCurrentView('papers')}
            title="Papers View"
          >
            P
          </button>
          <button
            className={`control-btn ${currentView === 'edges' ? 'active' : ''}`}
            onClick={() => setCurrentView('edges')}
            title="Edges View"
          >
            E
          </button>
          <button
            className={`control-btn ${currentView === 'tags' ? 'active' : ''}`}
            onClick={() => setCurrentView('tags')}
            title="Tags View"
          >
            T
          </button>
        </div>
      </div>

      {/* AI Panel */}
      {showAIPanel && (
        <div className="ai-panel visible">
          <div className="ai-header">
            <span className="ai-title">OpenAI O4-mini</span>
            <button className="close-btn" onClick={() => setShowAIPanel(false)}>
              ×
            </button>
          </div>

          {selectedPaper && (
            <div className="selected-paper-info">
              <div className="selected-paper-title">{selectedPaper.title}</div>
              <div className="selected-paper-authors">
                {selectedPaper.authors.join(', ')}
              </div>
            </div>
          )}

          <div className="ai-input-container">
            <input
              type="text"
              value={aiInput}
              onChange={(e) => setAiInput(e.target.value)}
              placeholder="Begin Typing..."
              className="ai-input"
            />
            <button className="summarize-btn" onClick={handleSummarize}>
              ⭐ Summarize
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GraphViewer;
